const init = () => {
  // Re-evaluate selectors to ensure DOM is ready
  const menuGrid = $("#menuGrid");
  if (!menuGrid) {
    console.error("Critical DOM elements not found. Retrying...");
    setTimeout(init, 100);
    return;
  }

  // Update selectors if needed
  selectors.menuGrid = menuGrid;

  selectors.year.textContent = String(new Date().getFullYear());

  // Load menu, ensure it has items
  const loadedMenu = storage.load(MENU_KEY, null);
  if (loadedMenu && Array.isArray(loadedMenu) && loadedMenu.length > 0) {
    state.menu = loadedMenu;
  } else {
    state.menu = [...defaultMenu];
    storage.save(MENU_KEY, state.menu);
  }

  // Ensure every menu item has an image (fix old saved data without images)
  const defaultImageById = Object.fromEntries(
    defaultMenu.map((item) => [item.id, item.image])
  );

  state.menu = state.menu.map((item) => ({
    ...item,
    image: defaultImageById[item.id] || item.image || "./assets/qr-code.svg",
  }));
  storage.save(MENU_KEY, state.menu);

  state.sales = storage.load(SALES_KEY, {}) || {};
  state.cart = storage.load(CART_KEY, []);

  console.log(
    "Initializing menu with",
    state.menu.length,
    "items:",
    state.menu.map((m) => m.name)
  );
  renderMenu(state.menu);
  renderAdminList();
  renderCart();
  renderMonthPicker();
  renderReport(selectors.monthPicker.value || monthKeyFromDate());
  updateHeroStats();
  wireEvents();
};

const wireEvents = () => {
  selectors.menuSearch.addEventListener("input", (event) => {
    const query = event.target.value.toLowerCase().trim();
    if (!query) {
      renderMenu(state.menu);
      return;
    }
    const filtered = state.menu.filter(
      (item) =>
        item.name.toLowerCase().includes(query) ||
        (item.category || "").toLowerCase().includes(query)
    );
    renderMenu(filtered);
  });

  selectors.manageBtn.addEventListener("click", () =>
    toggleModal(true, selectors.menuModal)
  );
  selectors.quickAddBtn.addEventListener("click", addTodaySpecial);

  selectors.menuModal.addEventListener("click", (event) => {
    if (event.target.matches("[data-close]")) {
      toggleModal(false, selectors.menuModal);
    }
  });
  selectors.qrModal.addEventListener("click", (event) => {
    if (event.target.matches("[data-close]")) {
      toggleModal(false, selectors.qrModal);
    }
  });

  selectors.menuForm.addEventListener("submit", handleMenuSave);
  selectors.resetMenuForm.addEventListener("click", resetMenuForm);
  selectors.deleteMenuItem.addEventListener("click", handleMenuDelete);

  selectors.cartItems.addEventListener("click", handleCartAction);
  if (selectors.clearCartBtn) {
    selectors.clearCartBtn.addEventListener("click", (e) => {
      e.preventDefault();
      clearCart();
    });
  }
  selectors.printBillBtn.addEventListener("click", () => {
    if (!state.cart.length) return alert("Cart is empty.");
    updatePrintableTicket(buildCartSnapshot());
    window.print();
  });
  selectors.payNowBtn.addEventListener("click", () => {
    if (!state.cart.length) return alert("Add items to bill before paying.");
    toggleModal(true, selectors.qrModal);
  });
  selectors.confirmPaymentBtn.addEventListener("click", finalizePayment);

  selectors.monthPicker.addEventListener("change", (event) =>
    renderReport(event.target.value)
  );
  selectors.exportExcelBtn.addEventListener("click", exportToExcel);

  // Image file upload handler
  selectors.menuItemImageFile.addEventListener("change", handleImageUpload);
  selectors.menuItemImage.addEventListener("input", handleImageUrlChange);

  document.addEventListener("keydown", (event) => {
    if (event.key === "Escape") {
      [selectors.menuModal, selectors.qrModal].forEach((modal) =>
        toggleModal(false, modal)
      );
    }
  });
};

const toggleModal = (isOpen, modal) => {
  modal?.setAttribute("aria-hidden", String(!isOpen));
};

const renderMenu = (menuItems) => {
  if (!selectors.menuGrid) {
    console.error("Menu grid element not found");
    return;
  }

  selectors.menuGrid.innerHTML = "";
  if (!menuItems || !menuItems.length) {
    selectors.menuGrid.innerHTML = `<p class="empty-state">No dishes match your search. Add new dishes from manage menu.</p>`;
    return;
  }

  const fragment = document.createDocumentFragment();
  menuItems.forEach((item) => {
    const card = document.createElement("article");
    card.className = "menu-card";
    card.innerHTML = `
      <img src="${item.image || "./assets/qr-code.svg"}" alt="${
      item.name
    }" loading="lazy" />
      <div class="menu-card__body">
        <h3>${item.name}</h3>
        <p>${item.category || "Special"}  ${formatCurrency(item.price)}</p>
        <button class="btn btn--primary" data-add="${
          item.id
        }">Add to bill</button>
      </div>
    `;
    fragment.appendChild(card);
  });
  selectors.menuGrid.appendChild(fragment);

  selectors.menuGrid.querySelectorAll("[data-add]").forEach((button) => {
    button.addEventListener("click", () => addToCart(button.dataset.add));
  });
};

const renderAdminList = () => {
  selectors.menuAdminList.innerHTML = "";
  if (!state.menu.length) {
    selectors.menuAdminList.innerHTML =
      "<li>No dishes found. Add one using the form.</li>";
    return;
  }
  const list = document.createDocumentFragment();
  state.menu.forEach((item) => {
    const li = document.createElement("li");
    li.innerHTML = `<span>${item.name}  ${formatCurrency(
      item.price
    )}</span><button data-edit="${item.id}">Edit</button>`;
    list.appendChild(li);
  });
  selectors.menuAdminList.appendChild(list);
  selectors.menuAdminList.querySelectorAll("button").forEach((button) => {
    button.addEventListener("click", () => loadMenuForm(button.dataset.edit));
  });
};

const handleImageUpload = (event) => {
  const file = event.target.files[0];
  if (!file) return;

  if (!file.type.startsWith("image/")) {
    alert("Please select an image file");
    return;
  }

  const reader = new FileReader();
  reader.onload = (e) => {
    const imageData = e.target.result;
    selectors.menuItemImage.value = imageData; // Store as data URL
    selectors.imagePreview.innerHTML = `<img src="${imageData}" alt="Preview" />`;
  };
  reader.readAsDataURL(file);
};

const handleImageUrlChange = (event) => {
  const url = event.target.value.trim();
  if (url && !url.startsWith("data:")) {
    selectors.imagePreview.innerHTML = `<img src="${url}" alt="Preview" onerror="this.parentElement.innerHTML=''" />`;
  } else if (!url) {
    selectors.imagePreview.innerHTML = "";
  }
};

const loadMenuForm = (id) => {
  const item = state.menu.find((menuItem) => menuItem.id === id);
  if (!item) return;
  selectors.menuFormTitle.textContent = `Edit ${item.name}`;
  selectors.menuItemId.value = item.id;
  selectors.menuItemName.value = item.name;
  selectors.menuItemPrice.value = item.price;
  selectors.menuItemCategory.value = item.category || "";
  selectors.menuItemImage.value = item.image || "";
  selectors.menuItemImageFile.value = ""; // Clear file input
  if (item.image) {
    handleImageUrlChange({ target: { value: item.image } });
  } else {
    selectors.imagePreview.innerHTML = "";
  }
};

const resetMenuForm = () => {
  selectors.menuFormTitle.textContent = "Add New Item";
  selectors.menuForm.reset();
  selectors.menuItemId.value = "";
  selectors.menuItemImageFile.value = "";
  selectors.imagePreview.innerHTML = "";
};

const handleMenuSave = (event) => {
  event.preventDefault();
  let imageValue = selectors.menuItemImage.value.trim();

  // If file was uploaded, use the data URL from the file input
  if (selectors.menuItemImageFile.files.length > 0) {
    // Already set in handleImageUpload
  }

  const payload = {
    id: selectors.menuItemId.value || crypto.randomUUID(),
    name: selectors.menuItemName.value.trim(),
    price: Number(selectors.menuItemPrice.value),
    category: selectors.menuItemCategory.value.trim(),
    image: imageValue || "./assets/qr-code.svg",
  };

  if (!payload.name || payload.price <= 0) {
    alert("Provide a valid name and price.");
    return;
  }

  const existingIndex = state.menu.findIndex((item) => item.id === payload.id);
  if (existingIndex >= 0) {
    state.menu[existingIndex] = payload;
  } else {
    state.menu.push(payload);
  }
  storage.save(MENU_KEY, state.menu);
  renderMenu(state.menu);
  renderAdminList();
  resetMenuForm();
  selectors.menuItemImageFile.value = ""; // Clear file input after save
};

const handleMenuDelete = () => {
  const id = selectors.menuItemId.value;
  if (!id) return alert("Select an item to delete.");
  if (!confirm("Delete this dish from menu?")) return;
  state.menu = state.menu.filter((item) => item.id !== id);
  storage.save(MENU_KEY, state.menu);
  renderMenu(state.menu);
  renderAdminList();
  resetMenuForm();
};

const addTodaySpecial = () => {
  const specials = ["Mini Tiffin", "Kesari Bath", "Podi Idly", "Paneer Dosa"];
  const pick = specials[Math.floor(Math.random() * specials.length)];
  selectors.menuItemName.value = pick;
  selectors.menuItemCategory.value = "Specials";
  selectors.menuItemPrice.value = (Math.random() * 80 + 40).toFixed(0);
  selectors.menuFormTitle.textContent = `Add ${pick}`;
  selectors.menuItemId.value = "";
  toggleModal(true, selectors.menuModal);
};

const saveCart = () => storage.save(CART_KEY, state.cart);

const addToCart = (id) => {
  const item = state.menu.find((menuItem) => menuItem.id === id);
  if (!item) return;
  const existing = state.cart.find((entry) => entry.id === id);
  if (existing) {
    existing.qty += 1;
  } else {
    state.cart.push({
      id: item.id,
      name: item.name,
      price: item.price,
      qty: 1,
    });
  }
  saveCart();
  renderCart();
};

const handleCartAction = (event) => {
  const action = event.target.dataset.action;
  if (!action) return;
  const id = event.target.closest("li")?.dataset?.id;
  if (!id) return;

  const entry = state.cart.find((item) => item.id === id);
  if (!entry) return;

  if (action === "inc") entry.qty += 1;
  if (action === "dec") entry.qty = Math.max(1, entry.qty - 1);
  if (action === "remove") {
    state.cart = state.cart.filter((item) => item.id !== id);
  }
  saveCart();
  renderCart();
};

const clearCart = () => {
  if (!state.cart || !state.cart.length) {
    console.log("Cart is already empty");
    return;
  }
  if (!confirm("Clear all items from the cart?")) return;
  state.cart = [];
  saveCart();
  renderCart();
  console.log("Cart cleared successfully");
};

const renderCart = () => {
  selectors.cartItems.innerHTML = "";
  if (!state.cart.length) {
    selectors.cartEmpty.hidden = false;
    return;
  }
  selectors.cartEmpty.hidden = true;

  const list = document.createDocumentFragment();
  state.cart.forEach((entry) => {
    const li = document.createElement("li");
    li.className = "cart-item";
    li.dataset.id = entry.id;
    li.innerHTML = `
      <div>
        <p class="cart-item__title">${entry.name}</p>
        <p class="cart-item__price">${formatCurrency(entry.price)} each</p>
      </div>
      <div class="qty">
        <button data-action="dec" aria-label="Decrease quantity"></button>
        <span>${entry.qty}</span>
        <button data-action="inc" aria-label="Increase quantity">+</button>
      </div>
      <div>
        <strong>${formatCurrency(entry.price * entry.qty)}</strong>
        <button class="icon-btn" data-action="remove" aria-label="Remove line"></button>
      </div>
    `;
    list.appendChild(li);
  });
  selectors.cartItems.appendChild(list);
  updateTotals();
};

const calculateTotals = () => {
  const subtotal = state.cart.reduce(
    (sum, entry) => sum + entry.price * entry.qty,
    0
  );
  const tax = subtotal * 0.05;
  const total = subtotal + tax;
  const itemCount = state.cart.reduce((sum, entry) => sum + entry.qty, 0);
  return { subtotal, tax, total, itemCount };
};

const updateTotals = () => {
  const { subtotal, tax, total, itemCount } = calculateTotals();
  selectors.cartTotals.count.textContent = itemCount;
  selectors.cartTotals.subtotal.textContent = formatCurrency(subtotal);
  selectors.cartTotals.tax.textContent = formatCurrency(tax);
  selectors.cartTotals.total.textContent = formatCurrency(total);
};

const buildCartSnapshot = () => {
  const { subtotal, tax, total } = calculateTotals();
  return {
    id: `BILL-${Date.now()}`,
    timestamp: Date.now(),
    items: state.cart.map((entry) => ({ ...entry })),
    subtotal,
    tax,
    total,
  };
};

const updatePrintableTicket = (bill) => {
  selectors.ticket.number.textContent = bill.id;
  selectors.ticket.date.textContent = new Date(bill.timestamp).toLocaleString(
    "en-IN",
    { dateStyle: "medium", timeStyle: "short" }
  );
  selectors.ticket.rows.innerHTML = bill.items
    .map(
      (item) =>
        `<tr><td>${item.name}</td><td>${item.qty}</td><td>${formatCurrency(
          item.price * item.qty
        )}</td></tr>`
    )
    .join("");
  selectors.ticket.subtotal.textContent = formatCurrency(bill.subtotal);
  selectors.ticket.tax.textContent = formatCurrency(bill.tax);
  selectors.ticket.total.textContent = formatCurrency(bill.total);
};

const finalizePayment = () => {
  if (!state.cart.length) return;
  const bill = buildCartSnapshot();
  const monthKey = monthKeyFromDate(new Date(bill.timestamp));
  const monthRecord = state.sales[monthKey] || {
    total: 0,
    orders: 0,
    entries: [],
  };

  monthRecord.total += bill.total;
  monthRecord.orders += 1;
  monthRecord.entries.push(bill);
  state.sales[monthKey] = monthRecord;
  storage.save(SALES_KEY, state.sales);

  updatePrintableTicket(bill);
  window.print();

  state.cart = [];
  saveCart();
  renderCart();
  renderMonthPicker();
  renderReport(monthKey);
  updateHeroStats();
  toggleModal(false, selectors.qrModal);
  alert("Payment recorded successfully!");
};

const renderMonthPicker = () => {
  const monthKeys = new Set([
    monthKeyFromDate(),
    ...Object.keys(state.sales || {}),
  ]);
  const options = Array.from(monthKeys)
    .sort((a, b) => (a > b ? -1 : 1))
    .map(
      (key) =>
        `<option value="${key}" ${
          key === selectors.monthPicker.value ? "selected" : ""
        }>${monthLabel(key)}</option>`
    )
    .join("");
  selectors.monthPicker.innerHTML = options;
};

const exportToExcel = () => {
  const monthKey = selectors.monthPicker.value || monthKeyFromDate();
  const record = state.sales[monthKey] || { total: 0, orders: 0, entries: [] };

  if (!record.entries.length) {
    alert("No data to export for this month.");
    return;
  }

  // Create CSV content (Excel can open CSV)
  let csv = "Date,Time,Bill ID,Items,Quantity,Subtotal,GST (5%),Total\n";

  record.entries.forEach((entry) => {
    const date = new Date(entry.timestamp);
    const dateStr = date.toLocaleDateString("en-IN", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
    const timeStr = date.toLocaleTimeString("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
    });

    entry.items.forEach((item, index) => {
      const isFirst = index === 0;
      csv += `"${isFirst ? dateStr : ""}","${isFirst ? timeStr : ""}","${
        isFirst ? entry.id : ""
      }","${item.name}","${item.qty}","${(item.price * item.qty).toFixed(
        2
      )}","${isFirst ? entry.tax.toFixed(2) : ""}","${
        isFirst ? entry.total.toFixed(2) : ""
      }"\n`;
    });
  });

  // Add summary row
  csv += `\n"Summary","","","","","","",""\n`;
  csv += `"Total Revenue","","","","","","","${record.total.toFixed(2)}"\n`;
  csv += `"Total Orders","","","","","","","${record.orders}"\n`;
  csv += `"Average Ticket","","","","","","","${(
    record.total / record.orders
  ).toFixed(2)}"\n`;

  // Create blob and download
  const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
  const link = document.createElement("a");
  const url = URL.createObjectURL(blob);
  link.setAttribute("href", url);
  link.setAttribute("download", `sales-report-${monthKey}.csv`);
  link.style.visibility = "hidden";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

const renderReport = (monthKey) => {
  const record = state.sales[monthKey] || { total: 0, orders: 0, entries: [] };
  selectors.reportRevenue.textContent = formatCurrency(record.total);
  selectors.reportOrders.textContent = record.orders;
  selectors.reportAverage.textContent = record.orders
    ? formatCurrency(record.total / record.orders)
    : formatCurrency(0);

  if (!record.entries.length) {
    selectors.reportRows.innerHTML = `<tr><td colspan="3" class="empty">No bills recorded this month.</td></tr>`;
    return;
  }
  selectors.reportRows.innerHTML = record.entries
    .slice()
    .reverse()
    .map(
      (entry) => `
      <tr>
        <td>${new Date(entry.timestamp).toLocaleDateString("en-IN", {
          day: "2-digit",
          month: "short",
        })}</td>
        <td>${entry.items.map((item) => item.name).join(", ")}</td>
        <td>${formatCurrency(entry.total)}</td>
      </tr>`
    )
    .join("");
};

const updateHeroStats = () => {
  const todayKey = monthKeyFromDate();
  const today = new Date();
  const todaysRevenue = (state.sales[todayKey]?.entries || []).reduce(
    (sum, entry) =>
      new Date(entry.timestamp).toDateString() === today.toDateString()
        ? sum + entry.total
        : sum,
    0
  );
  const todaysOrders = (state.sales[todayKey]?.entries || []).filter(
    (entry) => new Date(entry.timestamp).toDateString() === today.toDateString()
  ).length;
  selectors.statRevenue.textContent = formatCurrency(todaysRevenue);
  selectors.statOrders.textContent = todaysOrders;
};

document.addEventListener("DOMContentLoaded", init);
